mapper.select:
select

from t_banner
where 1=1

  and position=#{positionCode,jdbcType=VARCHAR}


  and platform=#{platform,jdbcType=INTEGER}


 and NOW() >=start_time


and NOW()<=end_time

and state=1
AND  app_code = #{appCode,jdbcType=VARCHAR}
ORDER BY rank

select title,rank,url,image_url,jump_type from t_banner where 1=1
and position=#{positionCode,jdbcType=VARCHAR}
and platform=#{platform,jdbcType=INTEGER}
AND app_code = #{appCode,jdbcType=VARCHAR}
and NOW() >=start_time
and NOW()<=end_time
and state=1 ORDER BY rank

select
banner.id, banner.title, banner.position, banner.start_time, banner.end_time, banner.rank, banner.url, banner.image_url,banner.state, banner.platform, banner.app_code, banner.jump_type,
banner.create_time, banner.update_time,app.app_name,pos.postion_name
from t_banner banner
left join  t_banner_app app on banner.app_code=app.app_code
left join  t_banner_position pos on banner.app_code=pos.app_code and pos.position_code =banner.position
where 1=1

  and banner.position=#{position,jdbcType=VARCHAR}


  and banner.platform=#{platform,jdbcType=INTEGER}


  and banner.state=#{state,jdbcType=INTEGER}


  AND  banner.app_code = #{appCode,jdbcType=VARCHAR}


  and banner.title like CONCAT('%', #{title,jdbcType=VARCHAR}, '%')


  and banner.position = #{positionCode,jdbcType=VARCHAR}


ORDER BY rank

mapper.insert:
insert into t_banner (id, title, position,
start_time, end_time, rank,
url, image_url, state,
platform, create_time, update_time
)
values (#{id,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{position,jdbcType=VARCHAR},
#{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{rank,jdbcType=INTEGER},
#{url,jdbcType=VARCHAR}, #{imageUrl,jdbcType=VARCHAR}, #{state,jdbcType=INTEGER}, #{app_code,jdbcType=INTEGER},
#{platform,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
)

mapper.update:
update t_banner
set state = #{newStatus,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER} and state = #{oldStatus,jdbcType=INTEGER}

mapper.select:
select

from t_banner
where id = #{id,jdbcType=INTEGER} and state = #{oldStatus,jdbcType=INTEGER}
